// executes when HTML-Document is loaded and DOM is ready
// -----------------------------------------------------------------------------
$(function() {
    //Estensione jQuery per animateCSS
    $.fn.extend({
        animateCss: function (animationName, fine) {
            var animationEnd = 'webkitAnimationEnd mozAnimationEnd MSAnimationEnd oanimationend animationend';
            this.addClass('animated ' + animationName).one(animationEnd, function() {
                $(this).removeClass('animated ' + animationName);
                if (fine === 1) {
                    $(this).css('display','none');
                } else if (fine === 2) {
                    $(this).css('visibility','visible');
                }
            });
            return this;
        }
    });

    //Attivazione lista funzioni
    init();
});

// executes when Windows is loaded
// -----------------------------------------------------------------------------
$(window).load(function() {

    // Animate loader
    if ($('.loader').length ) {
        setTimeout(function () {
            $('.loader').animateCss('fadeOut',1);
            $('.contenitore').animateCss('fadeIn',0);
        },300);
    }

});

function init() {

    toggleFilters();

    // Check if gallery product exists
    if ($('.xzoom').length ) {
        $('.xzoom, .xzoom-gallery').xzoom({zoomWidth: 400, title: false, position: 'inside', Xoffset: 15});
    }

}

// Add the sticky class to the navbar when you reach its scroll position. Remove "sticky" when you leave the scroll position
function stickyNavbar() {

    if (window.pageYOffset > sticky) {
        console.log('1');
        navbar.classList.add('sticky');
    } else {
        console.log('0');
        navbar.classList.remove('sticky');
    }
}

// Check windows width for show/hide filters in category page
function toggleFilters() {
    var widthHome = $(window).outerWidth(true);
    if ($('#collapseFilters').length ) {
        console.log('si');
        if (widthHome>768) {
            console.log('show');
            $('#collapseFilters').collapse('show');
        } else if (widthHome<768) {
            console.log('hide');
            $('#collapseFilters').collapse('hide');
        }
    }
}
